import jxl.*
import java.io.*
import jxl.write.*

String path="C:/Users/SaradaPrusty/Desktop/Windows7 Bckup/Badges_IBM/SDET Tester Manipal/API & Mobile Testing/REST/DataDrivenforAddingProduct.xls"
File excelFile=new File(path)

Workbook w=Workbook.getWorkbook(excelFile)
def sheet=w.getSheet("Sheet1")

def rowsize = sheet.getRows().toInteger()
def columnsize = sheet.getColumns().toInteger()

log.info "The total number of rows are $rowsize"
log.info "The total number of Columns are $columnsize"

def model = sheet.getCell(0,1).getContents()
def quantity = sheet.getCell(1,1).getContents()
def price = sheet.getCell(2,1).getContents()
def name = sheet.getCell(3,1).getContents()
def meta_title = sheet.getCell(4,1).getContents()

context.testCase.getTestStepByName("Properties").setPropertyValue("model",model)
context.testCase.getTestStepByName("Properties").setPropertyValue("quantity",quantity)
context.testCase.getTestStepByName("Properties").setPropertyValue("price",price)
context.testCase.getTestStepByName("Properties").setPropertyValue("name",name)
context.testCase.getTestStepByName("Properties").setPropertyValue("meta_title",meta_title)
//log.info meta_title

