import jxl.*
import java.io.*
import jxl.write.*
//To get the accessToken
def accessToken = context.expand('${#Project#Token}')

String path="C:/Users/SaradaPrusty/Desktop/Windows7 Bckup/Badges_IBM/SDET Tester Manipal/Assignment/REST Service Assignment/category&subcategoryidstore.xls"
File excelFile=new File(path)
Workbook w=Workbook.getWorkbook(excelFile)
def sheet=w.getSheet("Sheet1")
def rows = sheet.getRows()
log.info "Total number of rows are " +rows
for(def i=1;i<rows;i++)
{
    Cell data = sheet.getCell(0,i)
    log.info data.getContents()

    def get = new URL("http://rest-api.upskills.in/api/rest_admin/categories/"+data.getContents()).openConnection();
    get.setRequestProperty("Authorization", "bearer "+accessToken)
   def RC = get.getResponseCode();
   if(RC.equals(200))
   {
           //fetch teh response and print it
           log.info(get.getInputStream().getText());
    }
    else
     {
         log.info ("The Entered value is worng")
     }

}

import groovy.sql.Sql
def accessToken = context.expand('${#Project#Token}')
def url='jdbc:mysql://localhost:3306/RESTASSIGNMENT'
def driver='com.mysql.jdbc.Driver'
def user='root'
def password='root'

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)
def sql= Sql.newInstance(url,user,password,driver)
sql.eachRow("select * from category")
{
    row ->
    def category_id= "$row.category_id"    
    log.info "The Category ID is >>>   "+category_id
    def get = new URL("http://rest-api.upskills.in/api/rest_admin/categories/"+category_id).openConnection();
     get.setRequestProperty("Authorization", "bearer "+accessToken)
     def RC = get.getResponseCode();
     if(RC.equals(200))
     {
        //fetch teh response and print it
        log.info(get.getInputStream().getText());
     }
     else
    {
        log.info ("The Entered category_Id doesn't exist")
    }

}
sql.close()
